import { Button, Form, Modal } from "react-bootstrap";
import React from "react";
import { Note } from "../model/note";
import { useForm } from "react-hook-form";
import { NoteInput } from "../network/notesApi";

interface AddNoteProps {
  onDismiss: () => void;
  onNoteSaved: (note: Note) => void;
}
const AddNoteDialogue = ({ onDismiss, onNoteSaved }: AddNoteProps) => {
  const {} = useForm<NoteInput>();

  return (
    <Modal show onHide={onDismiss}>
      <Modal.Header closeButton>
        <Modal.Title>Add Note</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form id="addNoteForm">
          <Form.Group className="mb-3">
            <Form.Label>Title</Form.Label>
            <Form.Control type="text" placeholder="title" />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Text</Form.Label>
            <Form.Control as="textarea" rows={5} placeholder="text" />
          </Form.Group>
        </Form>
      </Modal.Body>

      <Modal.Footer>
        <Button type="submit" form="addNoteForm">
          Save
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddNoteDialogue;
